import React from 'react';

const GetProducto = ({producto}) => {

  const {id, title, price, category, description} = producto;

  return (
    <div key={id} className="flex border border-green-500 p-4 gap-4 mb-5">
      <p className="flex-1 border border-green-500 ">Nombre: {title}</p>
      <div className="flex-1 border border-green-500 ">Precio: {price}</div>
      <div className="flex-1 border border-green-500 ">Categoría: {category}</div>
      <div className="flex-1 border border-green-500 max-h-24 overflow-y-auto">Descripción: {description}</div>
      <button className="flex-1 border border-green-500 bg-gray-100 ">Eliminar</button>
      <button className="flex-1 border border-green-500 bg-gray-100 ">Modificar</button>
    </div>
  );
};

export default GetProducto;
